exports.make = function(memeImage, topText, bottomText) {
    if (!memeImage) return `ERROR 606: Image not found or entered.`
    if (!topText && !bottomText) return `ERROR 607: Neither top or bottom text was entered.`

    topText = topText.replace(/ /g, '-')
    bottomText = bottomText.replace(/ /g, '-')

    topText = topText.replace(/_/g, '__')
    bottomText = bottomText.replace(/_/g, '__')
    
    return `https://memegen.link/${memeImage}/${topText}/${bottomText}.jpg`
}

